import {AsyncStorage} from 'react-native';

export const SIGNUP = 'SIGNUP';
export const LOGIN = 'LOGIN';

export const signup = (email, password) => {
    return async dispatch => {
        const response = await fetch(
            'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyC1nLEcVJuHxtRPHEvpK93LRXSyStpWkFc',
            {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: email,
                    password: password,
                    returnSecureToken: true
                })
            }
            );
        
        if(!response.ok) {
            const errorResData = await response.json();
            const errorId = errorResData.error.message;
            let message = 'Something went wrong';
            if(errorId === 'EMAIL_EXISTS') {
                message = 'This email exists already';
            }
            throw new Error (message);
        }
        const resData = await response.Json();
        dispatch({ type: SIGNUP, token: resData.idToken, userId: resData.localId });
    };
};

export const login = (email, password) => {
    return async dispatch => {
        const response = await fetch(
            'https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyC1nLEcVJuHxtRPHEvpK93LRXSyStpWkFc',
            {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: email,
                    password: password,
                    returnSecureToken: true
                })
            }
            );
        
            if(!response.ok) {
                const errorResData = await response.json();
                const errorId = errorResData.error.message;
                let message = 'Something went wrong';
                if(errorId === 'EMAIL_NOT_FOUND') {
                    message = 'This email could not be found';
                } else if (errorId === 'INVALID_PASSWORD') {
                    message = 'This password in not valid';
                }
                throw new Error (message);
            }
        const resData = await response.Json();
        dispatch({ type: LOGIN, token: resData.idToken, userId: resData.localId   });
        const expirationDate = new Date().getTime() + parseInt(resData.expiresIn) * 1000
        saveDataToStorage(resData.idToken, resData.localId);
    };
};

const saveDataToStorage = (token, userId) => {
    AsyncStorage.setItem(
        'userData',
        JSON.stringify({
            token: token,
            userId: userId
        })
    );
}
